export default {
    name: "Login",
    data(){
        return{
            errFlag:false,
            login:null,
            password:null,
            valid:false,

            passwordRules: [
                v => !!v || 'Введите пароль',
                v => (v && v.length>=6) || 'Слишком короткий пароль'
            ],
            emailRules: [
                v => !!v || 'Введите email',
                v => /.+@.+/.test(v) || 'Неверный email'
            ]
        }
    },
    computed:{
        error(){
            return this.$store.getters.getError;
        },
        processing(){
            return this.$store.getters.getProcessing;
        },
        isUserAuthed(){
            return this.$store.getters.getIsAuthed;
        }
    },
    watch:{
        isUserAuthed(val){
            if (val===true){
                this.$router.push('/news');
                /*this.$router.push({name:'account',params:{userID}});*/
            }
        }
    },
    methods:{
        signin(){
            this.$store.dispatch('SIGNIN',{email:this.login, password:this.password})
        }
    }
}
